const htmlString = $input.first().json.body.storage.value; // Your HTML content
const h2Text = $('Split todo headers').first().json.todo; // Search phrase (even if interrupted by tags)

// Escape special regex characters in the text
const escapedText = h2Text.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');

// Match any <h2> that contains the desired phrase (even with HTML tags between words)
const h2Regex = new RegExp(
  `<h2[^>]*>[\\s\\S]*?${escapedText.split(/\s+/).join('[\\s\\S]*?')}[\\s\\S]*?<\\/h2>([\\s\\S]*?)(?=<h2[^>]*>|$)`,
  'i'
);

const match = htmlString.match(h2Regex);

if (!match) {
  return [{ json: { error: "Could not find matching h2 by text." } }];
}

const contentBetween = match[1].trim();

return [{ json: { extractedContent: contentBetween } }];

// This will take the stringified array in output and turn it into a real array
return [
  {
    json: {
      output: JSON.parse($json.output) // Convert string to array
    }
  }
];

