return items.map(item => {
	const data = item.json;

	// Safely pull Issue text from the HTML node
	const issueText = $('HTML').item.json.Issue ?? '';

	return {
		json: {
			// Pass through resolved_issues as-is, or empty array if missing
			resolved_issues: data.resolved_issues ?? [],

			// Convert "Todo" array-of-objects into array-of-strings, or empty array if missing
			todo: Array.isArray(data.Todo)
				? data.Todo.map(t => t.Todo ?? null).filter(Boolean)
				: [],

			// Add Issue text
			issue: issueText
		}
	};
});
